interface UseProviderCardReturn {
  copySuccess: boolean;
  expanded: boolean;
  downloadLoading: boolean;
  showFullDescription: boolean;
  copyToClipboard: () => Promise<void>;
  downloadManual: () => Promise<void>;
  setExpanded: (expanded: boolean) => void;
  setShowFullDescription: (show: boolean) => void;
}

export const useProviderCard = (provider: Provider): UseProviderCardReturn => {
  const [copySuccess, setCopySuccess] = useState(false);
  const [expanded, setExpanded] = useState(false);
  const [downloadLoading, setDownloadLoading] = useState(false);
  const [showFullDescription, setShowFullDescription] = useState(false);

  const copyToClipboard = async () => {
    try {
      const { metadata, ...providerWithoutMetadata } = provider;
      await navigator.clipboard.writeText(JSON.stringify(providerWithoutMetadata, null, 2));
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), UI_CONSTANTS.COPY_SUCCESS_TIMEOUT);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const downloadManual = async () => {
    if (provider.provider_type !== 'text' || !provider.file_path) return;

    setDownloadLoading(true);
    try {
      // Extract filename from file_path properly handling any path format
      const filename = provider.file_path.split(/[\\\/]/).pop() || provider.file_path;
      const response = await fetch(`/manuals/${filename}`);

      if (!response.ok) {
        throw new Error(`Failed to download: ${response.statusText}`);
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (err) {
      console.error('Failed to download manual:', err);
      alert('Failed to download manual file');
    } finally {
      setDownloadLoading(false);
    }
  };

  return {
    copySuccess,
    expanded,
    downloadLoading,
    showFullDescription,
    copyToClipboard,
    downloadManual,
    setExpanded,
    setShowFullDescription
  };
};

import { useState, useEffect } from 'react';
import type { Provider, CategoryCount } from '../types/provider';
import { UI_CONSTANTS } from '../constants/ui';

interface UseProvidersReturn {
  providers: Provider[];
  filteredProviders: Provider[];
  loading: boolean;
  error: string | null;
  searchTerm: string;
  selectedCategories: string[];
  setSearchTerm: (term: string) => void;
  setSelectedCategories: (categories: string[]) => void;
  toggleCategory: (category: string) => void;
  clearCategoryFilters: () => void;
  getCategories: () => CategoryCount[];
}

export const useProviders = (): UseProvidersReturn => {
  const [providers, setProviders] = useState<Provider[]>([]);
  const [filteredProviders, setFilteredProviders] = useState<Provider[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategories, setSelectedCategories] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Load providers from API
  useEffect(() => {
    const loadProviders = async () => {
      try {
        const response = await fetch('/providers.json');
        if (!response.ok) {
          throw new Error('Failed to load providers');
        }
        const data: Provider[] = await response.json();
        setProviders(data);
        setFilteredProviders(data);
      } catch (err) {
        console.error('Error loading providers:', err);
        setError(err instanceof Error ? err.message : 'Failed to load providers');
      } finally {
        setLoading(false);
      }
    };

    loadProviders();
  }, []);

  // Filter providers based on search term and selected categories
  useEffect(() => {
    let filtered = providers;

    // Filter by categories if selected
    if (selectedCategories.length > 0) {
      filtered = filtered.filter(provider => 
        selectedCategories.includes(provider.metadata?.category || UI_CONSTANTS.DEFAULT_CATEGORY)
      );
    }

    // Filter by search term
    if (searchTerm) {
      const lowerSearchTerm = searchTerm.toLowerCase();
      filtered = filtered.filter(provider =>
        provider.name.toLowerCase().includes(lowerSearchTerm) ||
        provider.provider_type.toLowerCase().includes(lowerSearchTerm) ||
        (provider.metadata?.description?.toLowerCase().includes(lowerSearchTerm)) ||
        (provider.metadata?.category?.toLowerCase().includes(lowerSearchTerm)) ||
        (provider.metadata?.maintainer?.toLowerCase().includes(lowerSearchTerm))
      );
    }

    setFilteredProviders(filtered);
  }, [searchTerm, selectedCategories, providers]);

  // Get categories with counts, sorted by popularity
  const getCategories = (): CategoryCount[] => {
    const categoryMap = new Map<string, number>();
    providers.forEach(provider => {
      const category = provider.metadata?.category || UI_CONSTANTS.DEFAULT_CATEGORY;
      categoryMap.set(category, (categoryMap.get(category) || 0) + 1);
    });

    return Array.from(categoryMap.entries())
      .sort(([,a], [,b]) => b - a) // Sort by count descending
      .map(([category, count]) => ({ category, count }));
  };

  // Toggle category selection
  const toggleCategory = (category: string) => {
    setSelectedCategories(prev => 
      prev.includes(category) 
        ? prev.filter(c => c !== category)
        : [...prev, category]
    );
  };

  // Clear all category filters
  const clearCategoryFilters = () => {
    setSelectedCategories([]);
  };

  return {
    providers,
    filteredProviders,
    loading,
    error,
    searchTerm,
    selectedCategories,
    setSearchTerm,
    setSelectedCategories,
    toggleCategory,
    clearCategoryFilters,
    getCategories
  };
};

